    public RTOExamData(Context context) {
        super(context, DATABASE_NAME, (SQLiteDatabase.CursorFactory) null, 5);
    }

    public void onCreate(SQLiteDatabase sQLiteDatabase) {
        this.dbase = sQLiteDatabase;
        sQLiteDatabase.execSQL("CREATE TABLE IF NOT EXISTS questen ( id INTEGER PRIMARY KEY AUTOINCREMENT, questionen TEXT, opta TEXT, optb TEXT, optc TEXT, answer INTEGER, imgpath TEXT)");
        sQLiteDatabase.execSQL("CREATE TABLE IF NOT EXISTS questhi ( id INTEGER PRIMARY KEY AUTOINCREMENT, questionen TEXT, opta TEXT, optb TEXT, optc TEXT, answer INTEGER, imgpath TEXT)");
        sQLiteDatabase.execSQL("CREATE TABLE IF NOT EXISTS questgu ( id INTEGER PRIMARY KEY AUTOINCREMENT, questionen TEXT, opta TEXT, optb TEXT, optc TEXT, answer INTEGER, imgpath TEXT)");
        addQuestions();
    }

    public void onUpgrade(SQLiteDatabase sQLiteDatabase, int i, int i2) {
        sQLiteDatabase.execSQL("DROP TABLE IF EXISTS quest");
        sQLiteDatabase.execSQL("DROP TABLE IF EXISTS questen");
        sQLiteDatabase.execSQL("DROP TABLE IF EXISTS questgu");
        sQLiteDatabase.execSQL("DROP TABLE IF EXISTS questhi");
        onCreate(sQLiteDatabase);
    }

    public String getTableFromLanID(Integer num) {
        if (num.intValue() == 1) {
            return TABLE_ENGLISH;
        }
        if (num.intValue() == 2) {
            return TABLE_HINDI;
        }
        return num.intValue() == 3 ? TABLE_GUJARATI : TABLE_ENGLISH;
    }

    public void addQuestion(RTO_Question rTO_Question, Integer num) {
        ContentValues contentValues = new ContentValues();
        contentValues.put(QUESTION, rTO_Question.getQUESTION());
        contentValues.put(OPTION_A, rTO_Question.getOPTA());
        contentValues.put(OPTION_B, rTO_Question.getOPTB());
        contentValues.put(OPTION_C, rTO_Question.getOPTC());
        contentValues.put(ANSWER, rTO_Question.getANSWER());
        contentValues.put(IMGPATH, rTO_Question.getIMAGEPATH());
        this.dbase.insert(getTableFromLanID(num), null, contentValues);
    }

    public List<RTO_Question> getAllQuestions(Integer num) {
        ArrayList arrayList = new ArrayList();
        this.dbase = getReadableDatabase();
        Cursor rawQuery = this.dbase.rawQuery("SELECT  * FROM " + getTableFromLanID(num), null);
        if (rawQuery.moveToFirst()) {
            do {
                RTO_Question rTO_Question = new RTO_Question();
                rTO_Question.setID(rawQuery.getInt(0));
                rTO_Question.setQUESTION(rawQuery.getString(1));
                rTO_Question.setANSWER(Integer.valueOf(rawQuery.getInt(5)));
                rTO_Question.setOPTA(rawQuery.getString(2));
                rTO_Question.setOPTB(rawQuery.getString(3));
                rTO_Question.setOPTC(rawQuery.getString(4));
                rTO_Question.setIMAGEPATH(rawQuery.getString(6));
                arrayList.add(rTO_Question);
            } while (rawQuery.moveToNext());
        }
        rawQuery.close();
        return arrayList;
    }

